import React, { useState, useRef, useEffect } from "react";



const BookingForm  = ({availableTimes, dispatch}) =>{

    const [date, setDate] = useState();
    const [time,setTime] = useState();
    const [guest, setGuest]  =useState();
    const [occasion, setOccasion] = useState();
    const addDates = async(date,time,guest,occasion) =>{
    await fetch('http:/localhost:8080/api/products/dates',{
        method: 'POST',
        body: JSON.stringify({
           date: date,
           time: time,
           guest:guest,
           occasion:occasion
        }),
        headers: {
           'Content-type': 'application/json; charset=UTF-8',
        },
        })
    }
    useEffect(()=>{

    },[])


    const ChangeDate = (e) => {
    setDate(e.target.value);
     };

    return(
    <form style={{"display": 'grid', "max-width": 200, "gap": 20}}>
        <label for="res-date" >Choose a Date</label>

        <input type = "date"
        id="res-date"
        onClick={ChangeDate}
         ></input>

        <label for ="res-time">Choose a Time</label>

        <select id = "res-time"
        onClick={(e)=>{setTime(e.target.value)}}>
        {availableTimes.map((time) =>(
            <option>{time}</option>
        ))}
        </select>

        <label for="guests" >Number of guests</label>

        <input type="number" placeholder="1" min="1" max="10" id="guests"
        onClick={(e)=> {setGuest(e.target.value)}}></input>

        <label for="occasion">Occasion</label>

        <select id="occasion"
        onClick={(e)=> {setOccasion(e.target.value)}} >
            <option>Birthday</option>
            <option>Anniversary</option>
        </select>

        <input type="submit" value="Make Your reservation"></input>
    </form>)

}


export default BookingForm;
