import React, { useState } from "react";

const onChangeDate = e => {
    const newDate = moment(new Date(e.target.value)).format('YYYY-MM-DD');
    setDate(newDate);
    console.log(newDate); //value picked from date picker
  };

const BookingForm  = () =>{
    const [date, setDate] = useState('');

    return(
    <form style={{"display": 'grid', "max-width": 200, "gap": 20}}>
        <label for="res-date" >Choose a Date</label>
        <input type = "date"
        id="res-date"
         value={date}
         onClick={(e) => setDate(e.target.value)}></input>
        <label for ="res-time">Choose a Time</label>
        <select id = "res-time">
        <option>17:00</option>
        <option>18:00</option>
        <option>19:00</option>
        <option>20:00</option>
        <option>21:00</option>
        <option>22:00</option>
        </select>
        <label for="guests">Number of guests</label>
        <input type="number" placeholder="1" min="1" max="10" id="guests"></input>
        <label for="occasion">Occasion</label>
        <select id="occasion">
            <option>Birthday</option>
            <option>Anniversary</option>
        </select>
        <input type="submit" value="Make Your reservation"></input>
    </form>)

}

export default BookingForm;
