import React, { useState, useRef, useReducer } from "react";




const BookingForm  = ({times}) =>{

    const instialTimes = times;
    const [selectedDate, setSelectedDate] = useState();

    const updateTimes = (state, action) =>{
        switch(action.type){
            case(selectedDate === "2023-07-07"):
            instialTimes =["17:00"]
            return instialTimes
            default:
            return instialTimes
        }
    }

    const [availableTimes, dispatch] = useReducer(updateTimes,instialTimes)
    const [date, setDate] = useState('');
    const [time,setTime] = useState();
    const [guest, setGuest]  =useState();
    const [occasion, setOccasion] = useState();


     const ChangeDate = (e) => {
    setDate(e.target.value);
     };

    return(
    <form style={{"display": 'grid', "max-width": 200, "gap": 20}}>
        <label for="res-date" >Choose a Date</label>

        <input type = "date"
        id="res-date"
         onClick={ChangeDate}
         ></input>

        <label for ="res-time">Choose a Time</label>

        <select id = "res-time"
        onClick={(e)=>{setTime(e.target.value)}}>
        {availableTimes.map((time) =>(
            <option>{time}</option>
        ))}
        </select>

        <label for="guests" >Number of guests</label>

        <input type="number" placeholder="1" min="1" max="10" id="guests"
        onClick={(e)=> {setGuest(e.target.value)
        console.log(guest)}}></input>

        <label for="occasion">Occasion</label>

        <select id="occasion"
        onClick={(e)=> {setOccasion(e.target.value)}} >
            <option>Birthday</option>
            <option>Anniversary</option>
        </select>

        <input type="submit" value="Make Your reservation"></input>
    </form>)

}


export default BookingForm;
