import {React, useEffect, useState} from "react"
import Cookies from "js-cookie";
import bruchetta from "../asset/bruchetta.png"

import {
  S3Client,
  PutObjectCommand,
  CreateBucketCommand,
  DeleteObjectCommand,
  DeleteBucketCommand,
  paginateListObjectsV2,
  GetObjectCommand,
} from "@aws-sdk/client-s3";

const Cart=()=>{
    var [data, setData] = useState([])
    useEffect(() =>{
        fetch('http://localhost:8080/api/getCart',{
            headers:{
                "token":Cookies.get('jwt_authorization')
            }
        })
        .then((response)=>response.json())
        .then((data)=>{
            setData(data)
        }).catch((err)=>{
            console.log(err.message);
        });
    },[])

    function getImage(num) {
        console.log(num)
        if(num ===1){
            return(<img  src = {bruchetta} alt ="MarioA" width= "200px" height="200px" />)
        }
    }


    return(
        <body>
            <ul>
                {data.map((item)=>{
                    return(
                    <li key={item.orderid}>{item.name} {item.price}
                    {getImage(item.id)}
                    </li>
                    )
                })}
            </ul>
        </body>
    )
    
}

export default Cart;
