import React, { useState, useRef } from "react";
import moment from "moment/moment";


const BookingForm  = () =>{
    const [date, setDate] = useState('');
    const dateInputRef = useRef(null);
    const [time,setTime] = useState();
    const timeInputRef = useRef(null);
    const [guest, setGuest]  =useState();


     const ChangeDate = (e) => {
    setDate(e.target.value);
    
     };

    return(
    <form style={{"display": 'grid', "max-width": 200, "gap": 20}}>
        <label for="res-date" >Choose a Date</label>

        <input type = "date"
        id="res-date"
         onClick={ChangeDate}
         ref = {dateInputRef}></input>

        <label for ="res-time">Choose a Time</label>

        <select id = "res-time"
        ref = {timeInputRef}
        onClick={(e)=>{setTime(e.target.value) 
        }}>
        <option>17:00</option>
        <option>18:00</option>
        <option>19:00</option>
        <option>20:00</option>
        <option>21:00</option>
        <option>22:00</option>
        </select>

        <label for="guests" >Number of guests</label>
        <input type="number" placeholder="1" min="1" max="10" id="guests"
        onClick={(e)=> {setGuest(e.target.value)}}></input>
        <label for="occasion">Occasion</label>
        <select id="occasion">
            <option>Birthday</option>
            <option>Anniversary</option>
        </select>
        <input type="submit" value="Make Your reservation"></input>
    </form>)

}


export default BookingForm;
